mapper.select:
select pd_id, pd_name, pd_price, pd_type, nvl(pd_sale, pd_price) pd_sale, pd_start, pd_end, pd_location, pr_img from (select rownum rn, pd_id, pd_name, pd_price, pd_type, pd_sale, pd_start , pd_end, pd_location, pr_img from (select pd_id, pd_name, pd_price, pd_type, pd_sale, pd_start, pd_end, pd_location, pr_img from product where 1=1 and
pd_type='PK'and pd_location= #{region}
order by pd_start
order by pd_sale
) a where rownum <= (#{page} * 10) ) b where b.rn > (#{page} - 1) * 10

mapper.update:
update product
set pd_name=#{pdName}, pd_price=#{pdPrice}, pd_sale=#{pdSale}, pd_start= #{pdStart}, pd_end= #{pdEnd} 
where pd_id=#{pdId}
